CREATE TABLE GeneratorSeries (Label NVARCHAR(50) NOT NULL, ObisCode INTEGER NOT NULl, BaseLabelId INTEGER NOT NULL, BaseObisId INTEGER NOT NULl, CostBreakdownTitle NVARCHAR(255) NOT NULL, PRIMARY KEY(Label, ObisCode), FOREIGN KEY(BaseLabelId) REFERENCES Label(Id), FOREIGN KEY (BaseObisId) REFERENCES Obis(Id)) WITHOUT ROWID;
